mapper.select:
SELECT USER_ID, USER_NAME, EMAIL
FROM USER_INFO
WHERE USER_ID = #{userId}
  AND PASSWORD = #{password}

SELECT CASE WHEN COUNT(1) = 0 THEN 'N' ELSE 'Y' END AS EXISTS_YN
FROM USER_INFO
WHERE USER_ID = #{userId}

SELECT CASE WHEN COUNT(1) = 0 THEN 'N' ELSE 'Y' END AS EXISTS_YN
FROM USER_INFO
WHERE EMAIL = #{email}
